// Inisialisasi aplikasi Firebase
firebase.initializeApp(firebaseConfig);

// Inisialisasi Firebase Authentication
var auth = firebase.auth();
var db = firebase.firestore();
var firestore = firebase.firestore();

// Mengambil total jumlah data pengguna yang telah login
auth.onAuthStateChanged(function (user) {
    if (user) {
        // Menambahkan data pengguna yang telah login ke Firestore
        firestore.collection('auth').doc(user.uid).set({
            uid: user.uid,
            email: user.email
        });
    }

    // Mengambil snapshot dari Firestore
    firestore.collection('auth').get().then(function (querySnapshot) {
        var total = querySnapshot.size;
        document.getElementById("user").textContent = total;
    });
});


// Mengambil total jumlah data dari Firestore
db.collection("/koraa/items/category/ux design/application").get().then(function (querySnapshot) {
    var total = querySnapshot.size;
    document.getElementById("application").textContent = total;
});