const form = document.getElementById('myForm');
const submitBtn = document.getElementById('submitBtn');
const readonlyInput = document.getElementById('img');

function checkInputs() {
    const readonlyValue = readonlyInput.value.trim();
    if (form.checkValidity() && ((!readonlyInput.hasAttribute('readonly') && readonlyValue !== '') || (readonlyInput.hasAttribute('readonly') && readonlyValue))) {
        submitBtn.disabled = false;
    } else {
        submitBtn.disabled = true;
    }
}

form.addEventListener('input', checkInputs);
readonlyInput.addEventListener('input', checkInputs);